contracts/Artifacts.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "./interfaces/IBlackList.sol";
import "./interfaces/IArtifacts.sol";
import "./interfaces/ITools.sol";

/// @title Artifact tokens can be aquired during mining
///        Artifacts are used to craft tools
contract Artifacts is
    Initializable,
    OwnableUpgradeable,
    ERC1155Upgradeable,
    IArtifacts,
    PausableUpgradeable
{
    /// @dev The address of the Blacklist contract
    IBlackList private _blacklist;
    /// @dev The address of the Tools contract
    ITools private _tools;

    /// @dev Base URI for IPFS
    string private _baseURI;

    /// @dev Number of types of artifacts
    /// @dev Starts with 0
    uint256 private _artifactTypes;

    /// @dev Mapping from artifact type to it's URI
    mapping(uint256 => string) private _typesToUris;

    /// @dev Checks that user is not in blacklist
    /// @param user The user to check
    modifier ifNotBlacklisted(address user) {
        require(!_blacklist.check(user), "Artifacts: user in blacklist");
        _;
    }

    function initialize(
        address _toolsContractAddress,
        string memory _baseUrl,
        address _blackListContractAddress
    ) external initializer {
        _tools = ITools(_toolsContractAddress);
        _baseURI = _baseUrl;
        _blacklist = IBlackList(_blackListContractAddress);

        __ERC1155_init("");
        __Ownable_init();
        __Pausable_init();
    }

    /// @notice See {IArtifacts-getArtifactsTypesAmount}
    function getArtifactsTypesAmount() external view returns (uint256) {
        return _artifactTypes;
    }

    /// @notice See {IArtifacts-getBaseUri}
    function getBaseUri() external view returns (string memory) {
        return _baseURI;
    }

    /// @notice See {IArtifacts-mint}
    function mint(
        uint256 artifactType,
        address to,
        uint256 amount
    ) external onlyOwner whenNotPaused ifNotBlacklisted(to) {
        require(
            artifactType <= _artifactTypes,
            "Artifacts: This artifact doesn't exist"
        );
        _mint(to, artifactType, amount, "");
    }

    /// @notice See {IArtifacts-mintBatch}
    function mintBatch(
        address to,
        uint256[] memory artifactTypes,
        uint256[] memory amounts
    ) external onlyOwner whenNotPaused ifNotBlacklisted(to) {
        for (uint256 counter = 0; counter < artifactTypes.length; counter++) {
            require(
                artifactTypes[counter] <= _artifactTypes,
                "Artifacts: this artifact type doesn't exists"
            );
        }

        _mintBatch(to, artifactTypes, amounts, "");
    }

    /// @notice See {IArtifacts-lootArtifact}
    function lootArtifact(
        address user,
        uint256 artifactType
    ) external ifNotBlacklisted(user) {
        require(
            _msgSender() == _tools.getMiningAddress(),
            "Artifacts: only mining contract can call this function"
        );
        _mint(user, artifactType, 1, "");
    }

    /// @notice See {IArtifacts-pause}
    function pause() external onlyOwner {
        if (!paused()) {
            _pause();
        } else {
            _unpause();
        }
    }

    /// @notice See {IArtifacts-addNewArtifact}
    function addNewArtifact(string memory newUri) external onlyOwner {
        _addNewArtifact(newUri);
    }

    /// @notice See {IArtifacts-setToolsAddress}
    function setToolsAddress(address toolsAddress) external onlyOwner {
        require(toolsAddress != address(0), "Artifacts: zero address");
        _tools = ITools(toolsAddress);
    }

    /// @notice See {IArtifacts-setBaseUri}
    function setBaseUri(string calldata newBaseUri) external onlyOwner {
        _setBaseUri(newBaseUri);
    }

    /// @notice See {IArtifacts-setUri}
    function setUri(
        uint256 artifactType,
        string calldata newUri
    ) external onlyOwner {
        _setUri(artifactType, newUri);
    }

    /// @notice See {IArtifacts-uri}
    function uri(
        uint256 artifactType
    )
        public
        view
        override(ERC1155Upgradeable, IArtifacts)
        returns (string memory)
    {
        require(
            artifactType <= _artifactTypes,
            "Artifacts: This artifact doesn't exist"
        );
        return _typesToUris[artifactType];
    }

    /// @dev Private implementation of `addNewArtifact`
    function _addNewArtifact(string memory newUri) private {
        _artifactTypes += 1;
        _tools.increaseArtifactsTypesAmount();
        // New artifact gets URI formed from base URI and uri from parameters
        // Example: ipfs://pinata.cloud/QmYqiEcxH58aTuQha2qxHp6c3zfv5NpNWxAhGQtGpBubwe
        _typesToUris[_artifactTypes] = string(
            abi.encodePacked(_baseURI, newUri)
        );
        emit AddNewArtifact(_artifactTypes, newUri);
    }

    /// @dev Private implementation of `setBaseUri`
    function _setBaseUri(string calldata newBaseUri) private {
        _baseURI = newBaseUri;
        emit BaseUriChanged(newBaseUri);
    }

    /// @dev Private implementation of `setUri`
    function _setUri(uint256 artifactType, string calldata newUri) private {
        require(
            artifactType <= _artifactTypes,
            "Artifacts: This artifact doesn't exist"
        );
        _typesToUris[artifactType] = newUri;
        emit UriChanged(artifactType, newUri);
    }
}

contracts/BlackList.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";

import "./interfaces/IBlackList.sol";

/// @title Blacklist contract. Blacklisted addresses cannot use main functions
///        of other contracts
contract BlackList is IBlackList, Ownable {
    /// @dev Mapping of users in the blacklist. If key value is True - user is blacklisted.
    ///      Otherwise, user is not blacklisted
    mapping(address => bool) private _blacklisted;

    /// @notice See {IBlacklist-check}
    function check(address user) external view returns (bool) {
        return _blacklisted[user];
    }

    /// @notice See {IBlacklist-addToBlacklist}
    function addToBlacklist(address user) external onlyOwner {
        require(_blacklisted[user] == false, "User already in blacklist");
        _blacklisted[user] = true;
        emit AddedToBlacklist(user);
    }

    /// @notice See {IBlacklist-removeFromBlacklist}
    function removeFromBlacklist(address user) external onlyOwner {
        require(_blacklisted[user] == true, "User is not in blacklist");
        _blacklisted[user] = false;
        emit RemovedFromBlacklist(user);
    }
}

contracts/Gem.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "./interfaces/IGem.sol";

/// @title Gem token contract
contract Gem is IGem, Ownable, ERC20 {
    /// @notice The address of the contract that can compensate fees with Gems
    address public compensator;
    /// @notice The BNB/GEM rate of compensation
    uint256 public compensationRate;

    /// @notice Addresses from this list cannot transfer of receive tokens
    mapping(address => bool) public blocked;

    constructor(uint256 compensationRate_) ERC20("GEM", "GEM") Ownable() {
        compensationRate = compensationRate_;
    }

    /// @notice See {IGem-setCompensator}
    function setCompensator(address compensator_) external onlyOwner {
        compensator = compensator_;
    }

    /// @notice See {IGem-setCompensationRate}
    function setCompensationRate(uint256 compensationRate_) external onlyOwner {
        compensationRate = compensationRate_;
    }

    /// @notice See {IGem-blockAddress}
    function blockAddress(address addr, bool isBlocked) external onlyOwner {
        blocked[addr] = isBlocked;

        if (isBlocked) {
            emit Blocked(addr);
        } else {
            emit Unblocked(addr);
        }
    }

    /// @notice See {IGem-compensateBnb}
    function compensateBnb(address to, uint256 bnbAmount) external {
        if (msg.sender == compensator) {
            _mint(to, (bnbAmount * compensationRate) / 10 ** 9);
        }
    }

    /// @notice See {IGem-decimals}
    function decimals() public pure override(ERC20, IGem) returns (uint8) {
        return 9;
    }

    /// @dev Checks that `to` is not blocked before transferring tokens
    /// @param from Sender of tokens
    /// @param to Receiver of tokens
    /// @param amount The amount of tokens sent
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal view override {
        require(!blocked[to], "Can not transfer to blocked addresses");
    }

    /// @dev Checks that `spender` is not blocked and approves
    ///      transfer of tokens
    /// @param owner The owner of tokens
    /// @param spender The spender of tokens
    /// @param amount The amount of tokens to spend
    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal override {
        require(!blocked[spender], "Can not approve blocked addresses");
        super._approve(owner, spender, amount);
    }
}

contracts/interfaces/IArtifacts.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";

/// @title Interface for the Artifacts contract
interface IArtifacts is IERC1155Upgradeable {
    event AddNewArtifact(uint256 artifactType, string newUri);
    event BaseUriChanged(string newBaseUri);
    event UriChanged(uint256 artifactType, string newUri);

    /// @notice Returns the amount of types of artifacts
    /// @return The amount of types of artifacts
    function getArtifactsTypesAmount() external view returns (uint256);

    /// @notice Returns the base URI for IPFS
    /// @return Base URI for IPFS
    function getBaseUri() external view returns (string memory);

    /// @notice Mints `amount` of artifacts of `artifactType` to `to`
    /// @param artifactType The type of artifact to mint
    /// @param to The receiver of artifacts
    /// @param amount The amount of artifacts to mint
    function mint(uint256 artifactType, address to, uint256 amount) external;

    /// @notice Mints batches of artifacts
    /// @param to The receiver of artifacts
    /// @param artifactTypes The types of artifacts to mint
    /// @param amounts The amount of artifacts of each type to mint
    function mintBatch(
        address to,
        uint256[] memory artifactTypes,
        uint256[] memory amounts
    ) external;

    /// @notice Mints a single artifact if Mining contract requests
    /// @param user The receiver of artifact
    /// @param artifactType The type of artifact to mint
    function lootArtifact(address user, uint256 artifactType) external;

    /// @notice Pauses contract if it's active. Activates it if it's paused
    function pause() external;

    /// @notice Adds a new artifact with the provided URI
    /// @param newUri The URI of a new artifact
    function addNewArtifact(string memory newUri) external;

    /// @notice Changes the address of Tools contract
    /// @param toolsAddress The new address of Tools contract
    function setToolsAddress(address toolsAddress) external;

    /// @notice Changes the base URI for IPFS
    /// @param newBaseUri The new base URI for IPFS
    function setBaseUri(string calldata newBaseUri) external;

    /// @notice Changes the URI for the specific artifact type
    /// @param artifactType The type of the artifact
    /// @param newUri The new URI
    function setUri(uint256 artifactType, string calldata newUri) external;

    /// @notice Returns the URI for a specific artifact type
    /// @param artifactType The type of the artifact to get a URI for
    /// @return The URI for a specific artifact type
    function uri(uint256 artifactType) external view returns (string memory);
}

