contracts/interfaces/ITools.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155Upgradeable.sol";

/// @title Interface for Tools contract
interface ITools is IERC1155Upgradeable {
    /// @dev Types of resources
    enum Resources {
        Berry, // a.k.a. FOOD
        Tree, // a.k.a. TECH
        Gold // a.k.a. AURA
    }

    /// @dev Recipe to craft a tool
    struct Recipe {
        // Amount of Tree to spend
        uint256 resourcesAmount;
        // Mapping (artifacts id => amonut)
        mapping(uint256 => uint256) artifacts;
    }

    /// @dev Struct of a tool
    struct Tool {
        uint32 strengthCost; // Cost of using this tool for mining. Strength decreases each time
        uint32 maxStrength; // Max strength of a tool
        uint32 miningDuration; // The duraion of mining session with this tool
        uint256 energyCost; // Cost in Berry tokens to start mining session with this tool
    }

    /// @dev Represents a tool owned by a user
    struct OwnedTool {
        uint128 toolType; // Type of the tool
        uint128 strength; // Current strength of the tool
    }

    /// @notice Indicates that a new tool was added
    /// @param toolType Type of the tool
    /// @param newURI URI of the tool
    event AddTool(uint256 toolType, string newURI);

    /// @notice Indicates that a tool was crafted
    /// @param user User who crafted a tool
    /// @param toolType Type of the tool
    /// @param toolId Unique ID of the tool
    event Craft(address user, uint256 toolType, uint256 toolId);

    /// @notice Indicates that tool recipe was created or updated
    /// @param toolType Type of the tool
    /// @param resourcesAmount The amount of Tree tokens to craft a tool
    /// @param artifactsAmounts The amount of artifacts to craft a tool
    event RecipeCreatedOrUpdated(
        uint256 toolType,
        uint256 resourcesAmount,
        uint256[] artifactsAmounts
    );

    /// @notice Indicates that a new base URI was set
    /// @param baseURI A new base URI
    event BaseURI(string baseURI);

    /// @notice Indicates that a tool has been fully repaired
    /// @param toolId The ID of the repaired tool
    event ToolRepaired(uint256 toolId);

    /// @notice Indicates that tool type's properties have been changed
    /// @param toolType The type of the tools to change the properties of
    event ToolPropertiesSet(uint256 toolType);

    /// @dev Indicates that `amount` of tools of `toolType` was minted to `to`
    /// @param to Receiver of tool
    /// @param toolType Type of the tool
    /// @param amount The amount of tools minted
    event MintType(address to, uint256 toolType, uint256 amount);

    /// @dev Indicates that one tool of `toolType` with `toolId` was minted to `to`
    /// @param to Receiver of tool
    /// @param toolType Type of the tool
    /// @param toolId The ID of the tool minted
    event MintId(address to, uint256 toolType, uint256 toolId);

    /// @dev Indicates that one tool of type `toolType` with `toolId` was transferred
    ///      from `from` to `to`
    /// @param from The sender of tokens
    /// @param to The receiver of tokens
    /// @param toolType Type of the tool
    /// @param toolId The ID of the tool transferred
    event Transfer(address from, address to, uint256 toolType, uint256 toolId);

    /// @notice Returns properties of the tool
    /// @param user User owning a tool
    /// @param toolId The ID of the tool
    /// @return toolType Type of the tool
    /// @return strength Current strength of the tool
    /// @return strengthCost Cost in strength to start mining with the tool
    /// @return miningDuration Duration of a mining session with the tool
    /// @return energyCost Cost in Berry tokens to start mining session with the tool
    function getToolProperties(
        address user,
        uint256 toolId
    )
        external
        view
        returns (
            uint256 toolType,
            uint256 strength,
            uint256 strengthCost,
            uint256 miningDuration,
            uint256 energyCost
        );

    /// @notice Returns properties of type of the tool
    /// @param toolType Type of the tool to get the properties of
    /// @return maxStrength Max strength of the tool type
    /// @return strengthCost Cost in strength to start mining with the tool
    /// @return miningDuration Duration of a mining session with the tool
    /// @return energyCost Cost in Berry tokens to start mining session with the tool
    function getToolTypeProperties(
        uint256 toolType
    )
        external
        view
        returns (
            uint256 maxStrength,
            uint256 strengthCost,
            uint256 miningDuration,
            uint256 energyCost
        );

    /// @notice Returns the address of resource contract of a specific resource type
    /// @param resourceId The type of resource
    /// @return The address of resource contract of a specific resource type
    function getResourceAddress(
        uint256 resourceId
    ) external view returns (address);

    /// @notice Returns the address of Artifacts contract
    /// @return The address of Artifacts contract
    function getArtifactsAddress() external view returns (address);

    /// @notice Returns current strength of the tool
    /// @param user The address of the owner of the tool
    /// @param toolId The ID of the tool
    /// @return Current strength of the tool
    function getStrength(
        address user,
        uint256 toolId
    ) external view returns (uint256);

    /// @notice Returns the address of Mining contract
    /// @return The address of Mining contract
    function getMiningAddress() external view returns (address);

    /// @notice Returns the amount of types of resources
    /// @return The amount of types of resources
    function getResourcesTypesAmount() external view returns (uint256);

    /// @notice Returns the amount of types of artifacts
    /// @return The amount of types of artifacts
    function getArtifactsTypesAmount() external view returns (uint256);

    /// @notice Returns the amount of types of tools
    /// @return The amount of types of tools
    function getToolsTypesAmount() external view returns (uint256);

    /// @notice Checks if user owns a tool with a given ID
    /// @param user The address of the user
    /// @param toolId The ID of the tool
    /// @return True if user owns a tool with a given ID. Otherwise - false
    function ownsTool(
        address user,
        uint256 toolId
    ) external view returns (bool);

    /// @notice Adds a new tool.
    /// @param maxStrength The maximum strength of the tool
    /// @param miningDuration The duration of mining session with the tool
    /// @param energyCost The cost in Berry tokens to start mining session with the tool
    /// @param strengthCost The cost in tool strength to start mining session with it
    /// @param resourcesAmount Amount of Tree tokens requires to craft a tool
    /// @param artifactsAmounts Amounts of each type of artifacts to craft a tool
    /// @param newURI The URI of the tool
    /// @return The type of the new tool
    function addTool(
        uint32 maxStrength,
        uint32 miningDuration,
        uint256 energyCost,
        uint32 strengthCost,
        uint256 resourcesAmount,
        uint256[] calldata artifactsAmounts,
        string calldata newURI
    ) external returns (uint256);

    /// @notice Changes properties of the tool
    /// @param toolType The type of the tool
    /// @param maxStrength The maximum strength of the tool
    /// @param miningDuration The duration of mining session with the tool
    /// @param energyCost The cost in Berry tokens to start mining session with the tool
    /// @param strengthCost The cost in tool strength to start mining session with it
    function setToolProperties(
        uint256 toolType,
        uint32 maxStrength,
        uint32 miningDuration,
        uint256 energyCost,
        uint32 strengthCost
    ) external;

    /// @notice Crafts a new tools after it was added
    /// @param toolType The type of the tool
    function craft(uint256 toolType) external;

    /// @notice Increases a number of types of artifacts by one
    function increaseArtifactsTypesAmount() external;

    /// @notice Decreases tool's strength when mining
    /// @param user The user who is mining
    /// @param toolId The ID of the tool used for mining
    /// @param strengthCost The amount of tool's strength subtracted from current strength
    function corrupt(
        address user,
        uint256 toolId,
        uint256 strengthCost
    ) external;

    /// @notice Changes address of Artifacts contract
    /// @param artifactsAddress The new address of Artifacts contract
    function setArtifactsAddress(address artifactsAddress) external;

    /// @notice Changes address of Mining contract
    /// @param miningAddress The new address of Mining contract
    function setMiningAddress(address miningAddress) external;

    /// @notice Pauses the contract if it's active. Activates it if it's paused
    function pause() external;

    /// @notice Mints `amount` tools of `toolType` to `to`
    /// @param to The receiver of tools
    /// @param toolType The type of the tool
    /// @param amount The amount of tools to mint
    function mint(address to, uint128 toolType, uint256 amount) external;

    /// @notice Mints batches of tools of different types to `to`
    /// @param to The receiver of tools
    /// @param toolTypes Types of tools
    /// @param amounts Amounts of tools
    function mintBatch(
        address to,
        uint256[] calldata toolTypes,
        uint256[] calldata amounts
    ) external;

    /// @notice Returns the recipe for the tool
    /// @param toolType The type of the tool
    /// @return resourcesAmount Amount of Tree resources to craft the tool
    /// @return artifactsAmounts Amounts of artifacts of different types to craft the tool
    function getRecipe(
        uint256 toolType
    )
        external
        view
        returns (uint256 resourcesAmount, uint256[] memory artifactsAmounts);

    /// @notice Returns the URI of the tool type
    /// @param toolType The type of the tool
    /// @return The URI of the tool type
    function uri(uint256 toolType) external view returns (string memory);

    /// @notice Changes the URI of the tool type
    /// @param toolType The type of the tool
    /// @param newURI The new URI
    function setURI(uint256 toolType, string calldata newURI) external;

    /// @notice Changes the base URI for tools
    /// @param baseURI The new base URI
    function setBaseURI(string calldata baseURI) external;

    /// @notice Changes the recipe of the tool
    /// @param toolType The type of the tool
    /// @param resourcesAmount The new amount of Tree to craft the tool
    function setRecipe(
        uint256 toolType,
        uint256 resourcesAmount,
        uint256[] calldata artifactsAmounts
    ) external;

    /// @notice Completely repairs the tool
    /// @param toolId The ID of the tool to repair
    function repairTool(uint256 toolId) external;

    /// @notice Transfers a single tool with `toolId` from `from` to `to`
    /// @param from Sender of tokens
    /// @param to Receiver of tokens
    /// @param toolId The ID of the tool to transfer
    /// @param amount Always equals to 1
    /// @param data Extra data (optional)
    function safeTransferFrom(
        address from,
        address to,
        uint256 toolId,
        uint256 amount,
        bytes memory data
    ) external;

    /// @notice Transfers one tool of each `toolIds` from `from` to `to`
    /// @param from Sender of tokens
    /// @param to Receiver of tokens
    /// @param toolIds IDs of tools to transfer
    /// @param amounts Each amount always equals to 1
    /// @param data Extra data (optional)
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory toolIds,
        uint256[] memory amounts,
        bytes memory data
    ) external;
}

contracts/interfaces/UniswapInterfaces.s.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.12;

/*

    This file contains interfaces of UniswapV2 system
*/

interface IUniswapV2Factory {
    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function feeTo() external view returns (address);

    function feeToSetter() external view returns (address);

    function getPair(
        address tokenA,
        address tokenB
    ) external view returns (address pair);

    function allPairs(uint256) external view returns (address pair);

    function allPairsLength() external view returns (uint256);

    function createPair(
        address tokenA,
        address tokenB
    ) external returns (address pair);

    function setFeeTo(address) external;

    function setFeeToSetter(address) external;
}

interface IUniswapV2Pair {
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(
        address owner,
        address spender
    ) external view returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(
        address indexed sender,
        uint256 amount0,
        uint256 amount1,
        address indexed to
    );
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function kLast() external view returns (uint256);

    function mint(address to) external returns (uint256 liquidity);

    function burn(
        address to
    ) external returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}

interface IUniswapV2Router01 {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB, uint256 liquidity);

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
        external
        payable
        returns (uint256 amountToken, uint256 amountETH, uint256 liquidity);

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountToken, uint256 amountETH);

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountToken, uint256 amountETH);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external pure returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(
        uint256 amountIn,
        address[] calldata path
    ) external view returns (uint256[] memory amounts);

    function getAmountsIn(
        uint256 amountOut,
        address[] calldata path
    ) external view returns (uint256[] memory amounts);
}

interface IUniswapV2Router02 is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountETH);

    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

contracts/Mining.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

import "./interfaces/ITools.sol";
import "./interfaces/IBlackList.sol";
import "./interfaces/IResources.sol";
import "./interfaces/IArtifacts.sol";
import "./interfaces/IMining.sol";

/// @title Contract for resources mining
contract Mining is
    Initializable,
    IMining,
    PausableUpgradeable,
    OwnableUpgradeable,
    IERC1155Receiver
{
    using ECDSA for bytes32;

    /// @dev The address of Tools contract
    ITools private _tools;
    /// @dev The address of Blacklist contract
    IBlackList private _blacklist;
    /// @dev Zero address to burn tokens
    address private constant _zeroAddress =
        0x000000000000000000000000000000000000dEaD;

    /// @dev Mapping (user address => (toolId => MiningSession))
    mapping(address => mapping(uint256 => MiningSession))
        private _usersToSessions;
    /// @dev Mapping showing the amount of resources a user would win in specific mining session
    // (user address => (toolId => (resource id => amount)))
    mapping(address => mapping(uint256 => mapping(uint256 => uint256)))
        private _usersToResources;
    /// @dev Mapping showing the amount of artifacts a user would win in specific mining session
    // (user address => (toolId => (artifact type => amount)))
    mapping(address => mapping(uint256 => mapping(uint256 => uint256)))
        private _usersToArtifacts;
    /// @notice Marks transaction hashes that have been executed already.
    ///         Prevents Replay Attacks
    mapping(bytes32 => bool) private _executed;

    /// @dev Checks that user is not blacklisted
    modifier ifNotBlacklisted(address user) {
        require(!_blacklist.check(user), "User in blacklist");
        _;
    }

    function initialize(
        address blacklistAddress,
        address toolsAddress
    ) external initializer {
        _tools = ITools(toolsAddress);
        _blacklist = IBlackList(blacklistAddress);

        __Pausable_init();
        __Ownable_init();
    }

    /// @dev The following 3 functions are required to ERC1155 standard
    function supportsInterface(
        bytes4 interfaceId
    ) external pure returns (bool) {
        return interfaceId == type(IERC165).interfaceId;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return
            bytes4(
                keccak256(
                    "onERC1155Received(address,address,uint256,uint256,bytes)"
                )
            );
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external pure override returns (bytes4) {
        return
            bytes4(
                keccak256(
                    "onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"
                )
            );
    }

    /// @notice See {IMining-pause}
    function pause() external onlyOwner {
        if (!paused()) {
            _pause();
        } else {
            _unpause();
        }
    }

    /// @notice See {IMining-startMining}
    function startMining(
        uint256 toolId,
        address user,
        bytes calldata rewards,
        bytes calldata signature,
        uint256 nonce
    ) external whenNotPaused ifNotBlacklisted(user) {
        (
            uint256[] memory resourcesAmount,
            uint256[] memory artifactsAmounts
        ) = abi.decode(rewards, (uint256[], uint256[]));

        // Avoid "stack too deep"
        Args memory args = Args({
            toolId: toolId,
            user: user,
            nonce: nonce,
            signature: signature,
            resources: resourcesAmount,
            artifacts: artifactsAmounts
        });

        require(
            !_usersToSessions[args.user][args.toolId].started,
            "Mining: this user already started mining process"
        );

        bytes32 txHash = _getTxHashMining(
            args.toolId,
            args.user,
            args.resources,
            args.artifacts,
            args.nonce
        );

        // Prevent signature replay attacks
        require(!_executed[txHash], "Mining: already executed");

        // Make sure the backend has signed the tx
        require(
            _verifyBackendSignature(args.signature, txHash),
            "Mining: invalid backend signature"
        );

        _executed[txHash] = true;

        (
            uint256 toolType,
            uint256 strength,
            uint256 strengthCost,
            uint256 miningDuration,
            uint256 energyCost
        ) = _tools.getToolProperties(args.user, args.toolId);

        require(strength - strengthCost > 0, "Mining: not enough strength");

        // Burn user's Berry tokens.
        IResources resource = IResources(_tools.getResourceAddress(0));
        resource.transferFrom(args.user, _zeroAddress, energyCost);

        // Transfer user's tool to this contract
        _tools.safeTransferFrom(args.user, address(this), args.toolId, 1, "");

        // Create a new session
        _usersToSessions[args.user][args.toolId] = MiningSession({
            toolId: uint32(args.toolId),
            started: true,
            ended: false,
            endTime: uint32(block.timestamp + miningDuration),
            energyCost: uint32(energyCost),
            strengthCost: uint16(strengthCost),
            nonce: uint32(args.nonce)
        });

        // After session has been started, the rewards are assigned to the user
        _setRewards(args.user, args.toolId, args.resources, args.artifacts);

        emit MiningStarted(args.user, _usersToSessions[args.user][args.toolId]);
    }

    /// @notice See {IMining-endMining}
    function endMining(
        uint256 toolId
    ) external whenNotPaused ifNotBlacklisted(_msgSender()) {
        require(
            _usersToSessions[_msgSender()][toolId].started,
            "Mining: user doesn't mine"
        );
        require(
            block.timestamp >= _usersToSessions[_msgSender()][toolId].endTime,
            "Mining: too early"
        );

        // Decrease tool's strength and give it back to the user
        _tools.corrupt(
            address(this),
            toolId,
            _usersToSessions[_msgSender()][toolId].strengthCost
        );
        _tools.safeTransferFrom(address(this), _msgSender(), toolId, 1, "");

        // Stop the session
        _usersToSessions[_msgSender()][toolId].ended = true;
        emit MiningEnded(_msgSender(), _usersToSessions[_msgSender()][toolId]);

        IResources resource;
        IArtifacts artifacts;

        uint256[] memory claimedResources = new uint256[](
            _tools.getResourcesTypesAmount()
        );
        uint256[] memory claimedArtifacts = new uint256[](
            _tools.getArtifactsTypesAmount()
        );

        // Claim all types of resources from this session
        for (
            uint256 toolType = 0;
            toolType < _tools.getResourcesTypesAmount();
            toolType++
        ) {
            if (_usersToResources[_msgSender()][toolId][toolType] != 0) {
                resource = IResources(_tools.getResourceAddress(toolType));
                resource.transfer(
                    _msgSender(),
                    _usersToResources[_msgSender()][toolId][toolType]
                );
                delete _usersToResources[_msgSender()][toolId][toolType];
            }
            // Mark that user has claimed some amount of resources. Even if it's zero.
            claimedResources[toolType] = _usersToResources[_msgSender()][
                toolId
            ][toolType];
        }

        // Claim all types of artifacts from this session
        for (
            uint256 artifactType = 0;
            artifactType < _tools.getArtifactsTypesAmount();
            artifactType++
        ) {
            if (_usersToArtifacts[_msgSender()][toolId][artifactType] != 0) {
                artifacts = IArtifacts(_tools.getArtifactsAddress());
                for (
                    uint256 i = 0;
                    i < _usersToArtifacts[_msgSender()][toolId][artifactType];
                    i++
                ) {
                    // Mint new artifact a required number of times
                    artifacts.lootArtifact(_msgSender(), artifactType);
                    // When looting, 1 artifact is minted
                    claimedArtifacts[artifactType] = 1;
                }
                delete _usersToArtifacts[_msgSender()][toolId][artifactType];
            } else {
                // No artifacts were looted
                claimedArtifacts[artifactType] = 0;
            }
        }

        emit RewardsClaimed(_msgSender(), claimedResources, claimedArtifacts);

        // Delete the session
        delete _usersToSessions[_msgSender()][toolId];
    }

    /// @dev Verifies that message was signed by the backend
    /// @param signature A signature used to sign the tx
    /// @param txHash An unsigned hashed data
    /// @return True if tx was signed by the backend (owner). Otherwise false.
    function _verifyBackendSignature(
        bytes memory signature,
        bytes32 txHash
    ) private view returns (bool) {
        // Remove the "\x19Ethereum Signed Message:\n" prefix from the signature
        bytes32 clearHash = txHash.toEthSignedMessageHash();
        // Recover the address of the user who signed the tx
        address recoveredUser = clearHash.recover(signature);
        return recoveredUser == owner();
    }

    /// @dev Calculates the hash of parameters of mining function and a nonce
    /// @param toolId The ID of the tool used for mining
    /// @param user The user who started mining
    /// @param resourcesAmount The amount of resources to be mined
    /// @param artifactsAmounts The amount of artifacts to be mined
    /// @param nonce The unique integer
    function _getTxHashMining(
        uint256 toolId,
        address user,
        uint256[] memory resourcesAmount,
        uint256[] memory artifactsAmounts,
        uint256 nonce
    ) private view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    address(this),
                    toolId,
                    user,
                    resourcesAmount,
                    artifactsAmounts,
                    nonce
                )
            );
    }

    /// @dev Sets rewards for a user in a specific mining session
    /// @param user The user to assign rewards to
    /// @param toolId The ID of the tool used in the session
    ///        a unique tool is used in each session
    /// @param resourcesAmount Array of amounts of each type of resource that
    ///        can be claimed after mining
    /// @param artifactsAmounts Array of amounts of each type of artifact that
    ///        can be claimed after mining
    function _setRewards(
        address user,
        uint256 toolId,
        uint256[] memory resourcesAmount,
        uint256[] memory artifactsAmounts
    ) private {
        for (uint256 counter = 0; counter < resourcesAmount.length; counter++) {
            if (resourcesAmount[counter] != 0) {
                _usersToResources[user][toolId][counter] += resourcesAmount[
                    counter
                ];
            }
        }
        for (
            uint256 counter = 0;
            counter < artifactsAmounts.length;
            counter++
        ) {
            if (artifactsAmounts[counter] != 0) {
                _usersToArtifacts[user][toolId][
                    counter + 1
                ] += artifactsAmounts[counter];
            }
        }
    }
}

